import{_ as a,c as t,o,b0 as r}from"./chunks/framework.JcZxIv1X.js";const l="/computer-docs/assets/image-20250918230140561.B_NIHaEe.png",s="/computer-docs/assets/image-20250918230244319.B5tzmx09.png",c="/computer-docs/assets/image-20250918231732286.DLQi6bYw.png",b=JSON.parse('{"title":"LV001-emu8086简介","description":null,"frontmatter":{"title":"LV001-emu8086简介","date":"2025-09-18T22:55:43.000Z","icon":"famicons:logo-markdown","permalink":"/sdoc/assembly/emu8086/126b08c9868713f16a6c6356","index":true,"tags":null,"categories":null,"copyright":false,"keywords":null,"cover":null,"comments":null,"mathjax":null,"top":null,"description":null,"tdoc":{"detailDate":"2025-09-18T22:55:43.319Z","fulluuid":"16a6c63565e84e0f9b768eacd79c8f8d","useduuid":"16a6c6356"}},"headers":[],"relativePath":"sdoc/assembly/emu8086/126b08c9868713f16a6c6356.md","filePath":"sdoc/01-Assembly/01-EMU8086/LV001-emu8086简介.md","lastUpdated":1760405208000}'),n={name:"sdoc/assembly/emu8086/126b08c9868713f16a6c6356.md"};function u(i,e,m,d,p,h){return o(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="lv001-emu8086简介" tabindex="-1">LV001-emu8086简介 <a class="header-anchor" href="#lv001-emu8086简介" aria-label="Permalink to &quot;LV001-emu8086简介&quot;">​</a></h1><h2 id="一、简介" tabindex="-1">一、简介 <a class="header-anchor" href="#一、简介" aria-label="Permalink to &quot;一、简介&quot;">​</a></h2><p>emu8086(8086汇编模拟工具)官方版是一款为学习和开发汇编语言提供理想环境的8086汇编模拟工具emu8086(8086汇编模拟工具)官方版让用户可以编写、调试和运行8086汇编程序，从而学习或研究8086微处理器的原理和应用。</p><p>EMU8086是一种学习汇编的工具，它结合了一个原始编辑器、组译器、反组译器、具除错功能的软件模拟工具（虚拟PC），还有一个循序渐进的指导工具。这对刚开始学组合语言的人会是一个很有用的工具。它会在模拟器中一步一步的编译程序码并执行，视觉化的工作环境让它更加容易使用。</p><blockquote><p>官网：<a href="https://emu8086.en.lo4d.com/windows" target="_blank" rel="noreferrer">Emu8086 - Download</a></p></blockquote><h2 id="二、安装eumu8086" tabindex="-1">二、安装eumu8086 <a class="header-anchor" href="#二、安装eumu8086" aria-label="Permalink to &quot;二、安装eumu8086&quot;">​</a></h2><h3 id="_1-下载安装包" tabindex="-1">1. 下载安装包 <a class="header-anchor" href="#_1-下载安装包" aria-label="Permalink to &quot;1. 下载安装包&quot;">​</a></h3><p>我这里直接官网下载，下载了个<a href="https://emu8086.en.lo4d.com/download/mirror-ls1" target="_blank" rel="noreferrer">Emu8086 V4.08</a></p><p><img src="'+l+'" alt="image-20250918230140561"></p><blockquote><p>Tips：<a href="https://wwyy.lanzouu.com/iPfW836iro0j" target="_blank" rel="noreferrer">https://wwyy.lanzouu.com/iPfW836iro0j</a></p></blockquote><h3 id="_2-安装软件" tabindex="-1">2. 安装软件 <a class="header-anchor" href="#_2-安装软件" aria-label="Permalink to &quot;2. 安装软件&quot;">​</a></h3><p>这个没啥难度，选择好最终的安装目录，一路默认即可，安装完毕后打开是这样的：</p><img src="'+s+'" alt="image-20250918230244319" style="zoom:50%;"><p>说实话，后面会发现在学习汇编过程中还是很好用的。这个软件并不是免费的，当然我们可以有自己的方法，网上搜一下，很多。</p><h3 id="_3-汉化" tabindex="-1">3. 汉化 <a class="header-anchor" href="#_3-汉化" aria-label="Permalink to &quot;3. 汉化&quot;">​</a></h3><p>找到汉化包中的<code>.exe</code>可执行文件，替换刚才安装目录中的<code>.exe</code>文件即可。</p><img src="'+c+'" alt="image-20250918231732286" style="zoom:50%;"><blockquote><p>官网下的在汉化后可能会有问题，可以网上直接找那种汉化版，一般都直接带有安装包。</p></blockquote><blockquote><p>参考资料：</p><p><a href="https://blog.csdn.net/qq_36528377/article/details/124989498" target="_blank" rel="noreferrer">emu8086入门指南：软件安装、汉化及实战教程-CSDN博客</a></p><p><a href="https://blog.csdn.net/qq_52127343/article/details/123722883" target="_blank" rel="noreferrer">带你极速掌握EMU8086汇编工具，深刻理解微机原理（含安装与快速上手教程）-CSDN博客</a></p></blockquote>',19)])])}const f=a(n,[["render",u]]);export{b as __pageData,f as default};
